(function() {
  var config, connectString, db, mongo;
  var __bind = function(fn, me){ return function(){ return fn.apply(me, arguments); }; };
  mongo = require('mongoskin');
  config = require('config');
  connectString = "http://" + config.hostname + ":" + config.port + "/";
  db = mongo.db("" + connectString + config.db);
  exports.dbExists = __bind(function(name, callback) {
    var dbtst;
    dbtst = mongo.db("" + connectString + name);
    return callback(dbtst != null);
  }, this);
  exports.dropDB = __bind(function(name, callback) {
    var dbdrop;
    dbdrop = mongo.db("" + connectString + name);
    return dbdrop.dropDatabase(name, function(err, done) {
      return callback(err, done);
    });
  }, this);
  exports.createDB = __bind(function(name, callback) {
    return mongo.db(name, function(err, done) {
      return callback(err, done);
    });
  }, this);
}).call(this);
